mapper.update:
UPDATE sucursal 
SET nombreSucursal = #{nombreSucursal}, telefono = #{telefono}, nombreEncargado = #{nombreEncargado}, 
idEstatus = #{idEstatus}, idEmpresa = #{idEmpresa}, direccion = #{direccion}, codigoPostal = #{codigoPostal}, 
colonia = #{colonia}, ciudad = #{ciudad}, latitud = #{latitud}, longitud = #{longitud}
WHERE idSucursal = #{idSucursal}

mapper.select:
SELECT p.* FROM promocion p
INNER JOIN sucursal s ON p.idSucursal = s.idSucursal
WHERE p.idSucursal = #{idSucursal}
